import React from "react"
import styled from "styled-components"
import Instrument from "./Instrument"
import { BrowserRouter, Route } from "react-router-dom"
import "./App.css"

const InstrumentContainer = styled("section")`
  display: flex;
  font-family: "Baloo Bhai", cursive;
  justify-content: center;
  align-items: center;
  height: 50vh;
  flex-wrap: wrap;
  width: 100%;
`

const Logo = styled("h1")`
  text-align: center;
  font-size: 5em;
  font-family: "Baloo Bhai", cursive;
`

const Desc = styled("h3")`
  font-style: italic;
  font-size: 3em;
  text-align: center;
  font-family: "Baloo Bhai", cursive;
`

const Img = styled("img")`
  max-width: 20vw;
  max-height: 20vh;
  margin-right: 30px;
  transition: transform 0.3s ease-in-out;
  :hover {
    transform: rotate(-20deg);
  }
`
const App = () => {
  const instruments = [
    {
      id: 1,
      src: "/images/instruments/violin.jpg",
      title: "Violin",
      desc: " lr"
    },
    {
      id: 2,
      src: "/images/instruments/viola.png",
      title: "Viola",
      desc: " lr"
    },
    {
      id: 3,
      src: "/images/instruments/cello.jpg",
      title: "Cello",
      desc: " lr"
    },
    { id: 4, src: "/images/instruments/bass.jpg", title: "Bass", desc: " lr" },
    {
      id: 5,
      src: "/images/instruments/flute.jpg",
      title: "Flute",
      desc: " lr"
    },
    { id: 6, src: "/images/instruments/oboe.jpg", title: "Oboe", desc: " lr" },
    {
      id: 7,
      src: "/images/instruments/clarinet.jpg",
      title: "Clarinet",
      desc: " lr"
    },
    {
      id: 8,
      src: "/images/instruments/bassoon.jpg",
      title: "Bassoon",
      desc: " lr"
    },
    {
      id: 9,
      src: "/images/instruments/trumpet.jpg",
      title: "Trumpet",
      desc: " lr"
    },
    {
      id: 10,
      src: "/images/instruments/frenchhorn.jpg",
      title: "French horn",
      desc: " lr"
    },
    { id: 11, src: "/images/instruments/tuba.jpg", title: "Tuba", desc: " lr" },
    {
      id: 12,
      src: "/images/instruments/trombone.jpg",
      title: "Trombone",
      desc: " lr"
    },
    {
      id: 13,
      src: "/images/instruments/percussion.jpg",
      title: "Percussion",
      desc: " lr"
    },
    { id: 14, src: "/images/instruments/harp.jpg", title: "Harp", desc: " lr" }
  ]

  const routes = instruments.map(({ title, id, desc }) => (
    <Route
      exact
      path={"/" + title.toLowerCase()}
      render={() => <Instrument title={title} desc={desc} />}
      key={id}
    />
  ))

  return (
    <>
      <BrowserRouter>
        {routes}
        <Route
          exact
          path={"/"}
          render={() => (
            <>
              <Logo>MusicZoo</Logo>
              <Desc> a first look into the world of instrumental music</Desc>
              <InstrumentContainer>
                {instruments.map(({ id, src, title }) => (
                  <Img key={id} src={src} title={title} alt={title} />
                ))}
              </InstrumentContainer>
            </>
          )}
        ></Route>
      </BrowserRouter>
    </>
  )
}

export default App
